import { useState, useEffect } from "react";
import { useAuth } from "../../context/auth";
import { Outlet } from "react-router-dom";
import axios from "axios";
import Spinner from "../Spinner";

export default function PrivateRoute() {
  const [ok, setOk] = useState(false);
  const [loading, setLoading] = useState(true); // Manage loading state
  const [auth, setAuth] = useAuth();

  useEffect(() => {
    const authCheck = async () => {
      try {
        const res = await axios.get(
          "http://localhost:3000/api/v1/auth/user-auth"
        );
        setOk(res.data.ok);
      } catch (error) {
        console.error("Error during auth check:", error);
        setOk(false); // Set to false on error
      } finally {
        setLoading(false); // Set loading to false regardless of success or failure
      }
    };

    if (auth?.token) authCheck();
    else {
      setLoading(false); // No token, set loading to false
    }
  }, [auth?.token]);

  if (loading) return <Spinner />; // Show spinner while loading

  return ok ? <Outlet /> : <Spinner />; // Show outlet or spinner based on auth check
}
